export declare const CANCEL_FULFILLMENT = "mutation cancelFulfillment($fulfillmentId: ID!) {\n    fulfillmentCancel(id: $fulfillmentId) {\n      fulfillment {\n        id\n        status\n      }\n    }\n  }\n";
export declare const CANCEL_ORDER = "\n  mutation cancelOrder($orderId: ID!) {\n    orderCancel(id: $orderId) {\n      order {\n        id\n        status\n      }\n    }\n  }\n";
export declare const CREATE_TRANSACTION = "\n  mutation createTransaction($externalOrderId: ID!) {\n    orderCreateTransaction(\n      input: {\n        id: $externalOrderId\n        kind: CAPTURE\n        source: EXTERNAL\n      }\n    ) {\n      transaction {\n        id\n        status\n      }\n    }\n  }\n";
export declare const INVENTORY_UPDATE = "\n  mutation inventoryUpdateAtShopifyForRTO($inventoryUpdateInput: InventoryLevelAdjustInput!) {\n    inventoryLevelAdjust(input: $inventoryUpdateInput) {\n      inventoryLevel {\n        id\n        available\n      }\n    }\n  }\n";
