export declare const GET_ORDER_DATA = "\n  query getOrderData($externalOrderId: ID!) {\n    order(id: $externalOrderId) {\n      id\n      name\n      email\n      paymentGatewayNames\n      totalPrice\n      lineItems(first: 5) {\n        \n          nodes {\n            title\n            quantity\n          }\n        \n      }\n    }\n  }\n";
export declare const GET_LOCATION_DATA = "\n  query getLocationData {\n    locations(first: 10) {\n        nodes {\n          id\n          name\n        }\n    }\n  }\n";
export declare const GET_ALL_PRODUCTS = "\n  query getAllProductList($limit: Int!) {\n    products(first: $limit) {\n    nodes {\n      id\n      title\n      vendor\n      variants (first: $limit){\n        nodes {\n          id\n          inventoryItem {\n            id\n          }\n        }\n      }\n    }\n  }\n  }\n";
export declare const GET_PRODUCT_DATA = "\n  query getProductData($productID: ID!) {\n    product(id: $productID) {\n  id\n  title\n  description\n  vendor\n  variants(first: 10) {\n    nodes {\n        inventoryItem {\n          id\n        }\n        id\n      }\n  }\n}\n  }\n";
export declare const GET_INVENTORY_ITEM_DATA = "\n  query getInventoryItemData($InventoryItemID: ID!) {\n    inventoryItem(id: $InventoryItemID) {\n      id\n      sku\n      tracked\n      harmonizedSystemCode\n    }\n  }\n";
export declare const GET_ACCESS_SCOPE_DATA = "\nquery {\n      currentAppInstallation {\n         accessScopes {\n      handle\n      description\n    }\n        }\n      }\n";
