export declare const CANCEL_FULFILLMENT = "mutation cancelFulfillment($fulfillmentId: ID!) {\n    fulfillmentCancel(id: $fulfillmentId) {\n      fulfillment {\n        id\n        status\n      }\n    }\n  }\n";
export declare const CANCEL_ORDER = "\n  mutation cancelOrder($orderId: ID!) {\n    orderCancel(id: $orderId) {\n      order {\n        id\n        status\n      }\n    }\n  }\n";
export declare const CREATE_TRANSACTION = "\n  mutation createTransaction($externalOrderId: ID!, $amount: Money!, $parentTransactionId: ID!) {\n   orderCapture(\n      input: {\n        id: $externalOrderId\n        amount: $amount\n        kind: SALE\n        parentTransactionId : $parentTransactionId\n      }\n    ) {\n      transaction {\n        id\n        status\n      }\n      userErrors {\n        field\n        message\n      }\n    }\n  }\n";
export declare const INVENTORY_UPDATE = "\n  mutation inventoryUpdateAtShopifyForRTO($available_adjustment: Int!, $location_id:number!,$name:String!, $reason: String!,$inventory_item_id:ID!) {\n    inventoryAdjustQuantities(input:  {\n     reason: $reason,\n     name: $name, \n     changes:{\n     delta: $available_adjustment, \n     inventoryItemId: $inventory_item_id, \n     locationId: $location_id\n     }\n    }) {\n      inventoryLevel {\n        id\n        available\n      }\n    }\n  }\n";
export declare const MOVE_ORDER_FULFILLMENT_LOCATION_MUTATION = "mutation movefulfillmentOrderlocation($id: ID! , $wherehouseAssignedLocationId: ID!){\n    fulfillmentOrderMove(id: $id, newLocationId: $wherehouseAssignedLocationId) {\n      movedFulfillmentOrder {\n        id\n        assignedLocation {\n          id\n        }\n      }\n      userErrors {\n        field\n        message\n      }\n    }\n      }\n  ";
export declare const CREATE_FULFILLMENT_MUTATION = "mutation createFulfillment($trackingNumber: String!, $trackingUrl: URL!, $trackingCompany: String!, $notifyCustomer: Boolean!, $fulfillmentOrderId: ID!) {\n    fulfillmentCreate(fulfillment: {\n      trackingInfo: {\n        number: $trackingNumber,\n        url: $trackingUrl,\n        company: $trackingCompany\n      },\n      notifyCustomer: $notifyCustomer,\n      lineItemsByFulfillmentOrder: [\n        {\n          fulfillmentOrderId: $fulfillmentOrderId\n        }\n      ]\n    }) {\n      fulfillment {\n        id\n        status\n      }\n      userErrors {\n        field\n        message\n      }\n    }\n  }";
export declare const FULFILLMENT_MUTATION_WITH_MULTIPLE_TRACKING_URLS = "mutation createFulfillment(\n    $trackingNumber: String!, \n    $trackingUrls: [String!]!, \n    $trackingCompany: String!, \n    $notifyCustomer: Boolean!, \n    $fulfillmentOrderId: ID!\n  ) {\n  \n    fulfillmentCreate(fulfillment: {\n      lineItemsByFulfillmentOrder: [\n        {\n          fulfillmentOrderId: $fulfillmentOrderId\n        }\n      ]\n      trackingInfo: {\n        number: $trackingNumber,\n        urls: $trackingUrls,\n        company: $trackingCompany\n      },\n      notifyCustomer: $notifyCustomer\n    }) {\n      fulfillment {\n        id\n        status\n      }\n      userErrors {\n        field\n        message\n      }\n    }\n  }";
export declare const WEBHOOK_MUTATION = " mutation webhookSubscriptionCreate($topic: WebhookSubscriptionTopic!, $address: URL!) {\n    webhookSubscriptionCreate(topic: $topic, webhookSubscription: {callbackUrl: $address, format: JSON}) {\n      userErrors {\n        field\n        message\n      }\n      webhookSubscription {\n        id\n        topic\n        apiVersion {\n          handle\n        }\n        format\n        createdAt\n      }\n    }\n  }\n";
