export declare const GET_ORDER_DATA = "\nquery getOrderData($externalOrderId: ID!) {\n  order(id: $externalOrderId) {\n    email\n    id\n    totalWeight\n    taxesIncluded\n    discountCodes\n    cancelledAt\n    displayFinancialStatus\n    billingAddress {\n      address1\n      address2\n      city\n      country\n      firstName\n      id\n      lastName\n      phone\n      province\n      zip\n      company\n    }\n    shippingAddress {\n      address1\n      address2\n      city\n      company\n      firstName\n      id\n      lastName\n      phone\n      province\n      zip\n      country\n      countryCode\n    }\n    lineItems (first:10){\n      nodes {\n        variant {\n          id\n        }\n        name\n        product {\n          id\n        }\n        quantity\n        taxLines {\n          rate\n          title\n          price\n        }\n        discountAllocations {\n          allocatedAmount {\n            amount\n          }\n        }\n        sku\n      }\n    }\n    taxLines {\n      price\n      rate\n      title\n    }\n    customer {\n      email\n      phone\n      lastName\n      id\n      firstName\n      createdAt\n      defaultAddress {\n        address1\n        address2\n        city\n        company\n        province\n        zip\n      }\n    }\n    shippingLines(first:10) {\n      nodes {\n        taxLines {\n          price\n        }\n        discountedPrice {\n          amount\n        }\n      }\n    }\n  }\n}\n";
